; } }return $hotkey; } } function gxl_get_hits($sidname,$type='hits',$array,$js=true){ if((C('url_html') && $js) || $type=='insert'){ return '<script type="text/javascript" src="'.C('site_path').'index.php?s=hits-show-id-'.$array[$sidname.'_id'].'-type-'.$type.'-sid-'.$sidname.'" charset="utf-8"></script>'; }else{ return $array[$type]; } } function gxl_arrays2array($array){ static $result_array=array(); foreach($array as $value){ if(is_array($value)){ gxl_arrays2array($value); }else{ $result_array[]=$value; } } return $result_array; } function gxl_detail_array($sid='ting', $type='next', $id, $cid, $field='ting_id,ting_cid,ting_status,ting_name,ting_jumpurl'){ $cache_key = 'cache_'.$sid.'_'.$type.'_'.$cid.'_'.$id; if(C('data_cache_ting')){ $array = S($cache_key); if($array){ return $array; } } $where = array(); $where[$sid.'_cid'] = $cid; $where[$sid.'_status'] = 1; if($type == 'next'){ $where[$sid.'_id'] = array('gt', $id); $order = $sid.'_id asc'; }else{ $where[$sid.'_id'] = array('lt', $id); $order = $sid.'_id desc'; } if($sid != 'ting'){ $field = str_replace('ting_',$sid.'_',$field); } $rs = M(ucfirst($sid)); $array = $rs->field($field)->where($where)->limit(1)->order($order)->find(); if( C('data_cache_ting') ){ S($cache_key, $array, intval(C('data_cache_ting'))); } return $array; } function UU($model,$params,$redirect=true,$suffix=false){ if(C('url_rewrite')){ if($params['p'] != '{!page!}'){ $params['p'] = '1'; } if($model == 'Home-ting/show'){ $reurl = str_replace(array('$id','$mcid','$page','$order','$picm','$listdir','$area','$year','$lz','$letter'),array($params['id'],$params['mcid'],$params['p'],$params['order'],$params['picm'],$params['listdir'],$params['area'],$params['year'],$params['lz'],$params['letter']),C('rewrite_tinglist')); } elseif($model == 'Home-ting/read'){ $reurl = str_replace(array('$id','$pinyin','$listdir'),array($params['id'],$params['pinyin'],$params['listdir']),C('rewrite_tingdetail')); }elseif($model == 'Home-ting/play'){ $reurl = str_